import("stdfaust.lib");

f = vslider("freq [scale:log] [unit:Hz]", 1, 0.1, 10, 0.01);
mix = vslider("ratio", 1, 0, 1, 0.01);

process = lfo1, square, sh, shsq with {
	lfo1 = os.osc(f/2);
  square = os.lf_squarewave(2*f);
	sh = no.noise : ba.sAndH(os.lf_imptrain(f/4));
  shsq = square, sh : si.interpolate(mix);
};

synth.code:
import("stdfaust.lib");

ui(e) = hgroup("LVmod", e);
uiX(e) = ui(vgroup("X", e));
uiY(e) = ui(vgroup("Y", e));
uiDelGroup(e) = vgroup("[9]Delay", e);

uiXFreq = uiX(hslider("[0]Freq [scale:log] [unit:Hz]", 42, 30, 1200, 0.01)) : qu.quantize(27.5, qu.penta) : si.smoo;
uiXBirth = uiX(hslider("[1]Birth", 0.1, 0, 1, .000001)) : ^(2) : *(10) : si.smoo;
uiXFreedom = uiX(hslider("[3]Freedom [scale:log]",  1, 0.1, 1024, .000001)) : si.smoo;
uiXGrowth = uiX(hslider("[4]Growth", 0.1, -1, 1, .000001)) : ^(3) : *(2) : si.smoo;
uiXInter = uiX(hslider("[5]Interaction", -0.46, -1.5, 1.5, .000001)) : ^(5) : si.smoo;
uiXDecay = uiX(hslider("[6]Decay [scale:log] [unit:Hz]", 10, 0.1, 55, 0.001)) : si.smoo;

uiYFreq = uiY(hslider("[0]Freq [scale:log] [unit:Hz]", 42, 30, 1200, 0.01)) : qu.quantize(27.5, qu.penta) : si.smoo;
uiYBirth = uiY(hslider("[1]Birth", 0.1, 0, 1, .000001)) : ^(2) : *(10) : si.smoo;
uiYFreedom = uiY(hslider("[3]Freedom [scale:log]",  1, 0.1, 1024, .000001)) : si.smoo;
uiYGrowth = uiY(hslider("[4]Growth", 0.1, -1, 1, .000001)) : ^(3) : *(2) : si.smoo;
uiYInter = uiY(hslider("[5]Interaction", -0.46, -1.5, 1.5, .000001)) : ^(5) : si.smoo;
uiYDecay = uiY(hslider("[6]Decay [scale:log] [unit:Hz]", 10, 0.1, 55, 0.001)) : si.smoo;

osc(f) = no.noise : fi.nlf2(f, 0.99999) : _, ! : /(100);

sat(lim, input) = input : /(lim) : ma.tanh : *(lim);

dcblock(f, input) = input : fi.highpass(1, f);

icompute(sig, aux, birth, inter, growth, lim, decay, fb) = smix : sat(lim) : dcblock(decay) with {
    smix = sig * birth + aux * inter + fb * (1 + growth);
};

compute(sig, aux, birth, inter, growth, lim, decay) = icompute(sig, aux, birth, inter, growth, lim, decay) ~ _;

loop(x_in, y_in, xy_in) = (x*y), x/uiXFreedom, y/uiYFreedom with {
    x = compute(x_in, xy_in, uiXBirth, uiXInter, uiXGrowth, uiXFreedom, uiXDecay);
    y = compute(y_in, xy_in, uiYBirth, uiYInter, uiYGrowth, uiYFreedom, uiYDecay);
};

lv(x, y) = (loop(x, y) ~ _) : !,_,_;

oscs = osc(uiXFreq), osc(uiYFreq) : _,_;

process = oscs : lv : ma.tanh, ma.tanh;
