modulation.code:
import("stdfaust.lib");

ui(x) = tgroup("Euclid Van der Pol", x);
uiseq(x) = ui(hgroup("[1]Clock", x));
uirung(x) = ui(hgroup("[2]Rungler", x));
uiother(x) = ui(hgroup("[3]Other", x));

N = uiseq(vslider("[0]Euclid N", 15, 2, 21, 1));
CLK = uiseq(vslider("[0]Clock [scale:log] [unit: bpm]", 200, 10, 900, 0.1)) : /(60) : si.smoo;
K1 = uiseq(vslider("[1]Euclid K1", 2, 2, 21, 1)) : min(N);
K2 = uiseq(vslider("[4]Euclid K2", 7, 2, 21, 1)) : min(N);
// K3 = uiseq(vslider("[7]K3", 9, 2, 21, 1)) : min(N);

euclid(n, k, clock) = clock : counter : _ with {
    counter(imp) = (_counter ~ (_,_))(imp) : !,!,_ with {
        _counter(_i, _e, imp) = i, e, trig with {
            i = (_i + imp) % n;
            e = ba.if(imp, descent, _e);
            descent = (k * _i) % n >= (k * i) % n;
            trig = imp * e;
        };
    };
};

mclk = os.lf_sawpos(CLK);

eclk = mclk : impulsify <: e1, e2 with {
  impulsify(ramp) = ramp <: -(mem)<0;
  e1(clk) = clk : euclid(N, K1);
  e2(clk) = clk : cdiv(uiRatio) : euclid(N, K2); 
  // e3(clk) = clk : euclid(N, K3); 
};


// rungler circuit (7-bit register, 3-bit dac)
rungler73(shuf, data, clock) = data : shift : dac with {
    // shift register
    shift = head : tail with {
        // first cells simply pass their output forward
        head = latch(clock : @(6)) : latch(clock : @(5)) : latch(clock : @(4)) : latch(clock : @(3));

        // last 3 cells split their output (to be fed into a 3-bit DAC) 
        tail = latch(clock : @(2)) <: _, (latch(clock : @(1)) <: _, latch(clock));

        // latch cell implementation
        latch(trig, in) = (trig, in) : (cell ~ _) with {
            cell(val, trig, in) = select2(trig, val, in) : _;
        };
    };
    // only sample new shuf values on clock signal
    sshuf = shuf : ba.sAndH(clock);

    dac(x, y, z) = 4*x + shuffle(2*y + z) : int with {
        // shuffle input bits based on a seed
        shuffle(v) = ((v + a) * b) % 4 with {
            a = int(sshuf/2);
            b = select2(sshuf%2, 1, 3);
        };
    };
};


// utility function
clip(x) = select2(x>1, x, 1);

// clock divider
cdiv(ratio, in) = select2(ratio==1, div, in) with {
    div = in : (inc(ratio) ~ _) <: -(mem)<0 with {
        inc(r, current, in) = current + in/r : clip : ma.frac;
    };
};


// polyrhythm setup
pr = 2 * (int(uiShape * 2.99) + 1) + 1;
prs = 2 * pr * uiRatio;


// main sampling clocks
// clk = os.lf_imptrain(uiClockFreq * pow(1.3, no.lfnoise(0.07)) * pow(1.1, no.lfnoise(2.31))) <: _, cdiv(prs) : poly(2,pr), poly(2,pr) : _,_ with {
//     // polyrhythm generator
//     poly(r1, r2) = _ <: cdiv(r1), cdiv(r2) :> clip;
// };


// data line
data = os.lf_imptrain(df) <: _, cdiv(prs) : sq, sq : _,_ with {
    sq(imp) = imp : (toggle ~ _) with {
        toggle(state, imp) = ba.if(imp>0, 1-state, state);
    };
    // data line frequency
    df = si.interpolate(cycle(uiShape), 1, 11) * 2 with {
        // cycles from 0 to 1 two times when x goes 0..1
        cycle(x) = 1 - 0.5 * (1 + cos(x * 4 * ma.PI));
    };
};

rungler = eclk, data : router : rungler73(shuf), rungler73(shuf) : mask : norm, norm  with {
    router(c1, c2, d1, d2) = d1, c1, d2, c2;

    // dac shuffle covers integer range [0..7] when shape goes 0..1
    shuf = int(uiShape * 7.99);

    // "flatten" pwm duty cycle to make it spend more time closer to 0.5  
    // duty = 0.5 * (1 + (2*density-1)^3);

    // density mask (sample and hold via pwm wave)
    mask = skip, _ with {
        skip(x) = x : ba.sAndH(os.lf_pulsetrainpos(pwmff(norm(x)), uiDensity)) with {
            // rungler output value sets the 'density' complex parameter
            pwmff(xdens) = fbase * fm with {
                // pwm base frequency rushes from f down to 0.1 when desity goes from 1 to 0
                fbase = si.interpolate(xdens^5, 0.1, uiClockFreq);

                // vary pwm frequency more aggressively in certain ranges
                entropy = (0.5 * (1 + sin(xdens * 1.5 * ma.PI)))^3;

                // leads to 'bursts' of slowly varying length
                fm = pow(1.3, entropy * no.lfnoise(0.1 + xdens));
            };
        };
    };

    norm(x) = x / 7;
};

uiClockFreq = CLK * 4; // hslider("[1]Clock", 11.61, 0.1, 20, 0.01);
uiShape = uirung(hslider("[2]Data", 0.22, 0, 1, 0.01));
uiDensity = uirung(hslider("[3]Density", 0.7, 0, 1, 0.01));
uiRatio = uirung(hslider("[4]Fugue Ratio", 2, 1, 16, 1)) : int;

lfnoise = no.lfnoise(f) with {
  f = hslider("[1] LFN Freq [scale:log] [unit:Hz]", 1, 0.001, 100, 0.001) : si.smoo;
};

process = (r1, r2, lfn) with {
  r1 = rungler : _,!;
  r2 = rungler : !,_;
  lfn = uiother(lfnoise);
};

synth.code:
import("stdfaust.lib");

ui(x) = tgroup("Euclid Van der Pol", x);
uiseq(x) = ui(hgroup("[1]Sequencer", x));

N = uiseq(vslider("[0]N", 15, 2, 21, 1));
CLK = uiseq(vslider("[0]Clock [scale:log] [unit: bpm]", 200, 10, 900, 0.1)) : /(60) : si.smoo;
K1 = uiseq(vslider("[1]K1", 2, 2, 21, 1)) : min(N);
K2 = uiseq(vslider("[4]K2", 7, 2, 21, 1)) : min(N);
K3 = uiseq(vslider("[7]K3", 9, 2, 21, 1)) : min(N);

clock = os.lf_imptrain(CLK);

/* 
ideal syntax below (maybe write a pre-processor)

counter(imp) = trig with {
    i = (i' + imp) % n;
    e = ba.if(imp, descent, e');
    descent = (k * i') % n >= (k * i) % n;
    trig = imp * e;
}
*/

euclid(n, k, clock) = clock : counter : _ with {
    counter(imp) = (_counter ~ (_,_))(imp) : !,!,_ with {
        _counter(_i, _e, imp) = i, e, trig with {
            i = (_i + imp) % n;
            e = ba.if(imp, descent, _e);
            descent = (k * _i) % n >= (k * i) % n;
            trig = imp * e;
        };
    };
};

uio1(x) = ui(hgroup("[2]Osc 1", x));
uio2(x) = ui(hgroup("[3]Osc 2", x));
uio3(x) = ui(hgroup("[4]Osc 3", x));

quant = qu.quantize(55, qu.mixo);

// Forced Van der Pol oscillator
fvdp(trig) = trig : resonator <: dry, wet : interprod with {
  dry = _ : *(uiDrive) : ma.tanh;
  wet = _ : (loop ~ (_,_)) : par(i, 2, out) : spread;  

  // driving source
  resonator(trig) = trig : pm.strike(0.2, 0.2, 0.5) : fi.nlf2(uiFreq, 1 - 1/uiQ) : _,!;

  // signal conditioning block
  // (saturation with variable limit) -> (dc blocker with variable frequency)
  sblock(x) = x : /(uiLim) : ma.tanh : *(uiLim) : fi.highpass(1, uiHP);

  loop(x, y, osc) = sblock(x1), sblock(y1) with {
    x1 = x + uiDt * y;
    y1 = y + uiDt * (uiEps * y * (1 - x^2) - x + uiForce * osc);
  };

  out = /(uiLim) : *(uiDrive) : ma.tanh;

  spread(x, y) = x, y : si.interpolate(uiSpread);

  // 3-way mixer, interpolates between 3 signals based on a [0..1] value
  mix3(v, x, y, z) = gX * x + gY * y + gZ * z with {
      gX = ba.listInterp((1, 0, 0), v * 2);
      gY = ba.listInterp((0, 1, 0), v * 2);
      gZ = ba.listInterp((0, 0, 1), v * 2);
  };

  // interpolate between two signals through their product
  interprod(a, b) = mix3(uiDryWet, a, a*b, b);

  uiFreq = vslider("[1]Freq [unit:Hz] [scale:log]", 55, 0.1, 16000, 0.01) : quant : si.smoo;
  uiQ = vslider("[2]Q", 14, 1.1, 20, 0.01) : ^(4) : si.smoo;

  uiForce = vslider("[3]Force", 0.5, 0.001, 1, 0.01) : ^(2) : *(100) : si.smoo;
  uiEps = vslider("[4]Feedback", 0.1, 0, 1, 0.00001) : ^(2) : *(50) : si.smoo;
  uiDt = vslider("[5]dt", 1, 0.1, 20, 0.001) : /(50) : si.smoo;
  uiLim = vslider("[6]Limit",  0.1, 0.0001, 1, .000001) : ^(2) : *(100) : si.smoo;
  uiHP = vslider("[7]DC Block [scale:log]", 10, 0.5, 220, 0.001) : si.smoo;

  uiSpread = vslider("[81]Spread", 0.42, 0, 1, 0.01) : si.smoo;
  uiDryWet = vslider("[82]DistAmt", 0.21, 0, 1, 0.01) : si.smoo;

  uiDrive = vslider("[90]Drive", 0.3, 0, 1, 0.001) : ^(4) : *(11) : si.smoo;
};

eseq(clock) = clock <: euclid(N, K1), euclid(N, K2), euclid(N, K3);

process = clock : eseq : (!, uio2(fvdp), !) <: ui(dm.zita_light) : _,_;
