import("stdfaust.lib");

f = vslider("freq [scale:log] [unit:Hz]", 1, 0.01, 10, 0.01);
mix = vslider("ratio", 1, 0, 1, 0.01);

process = lfo1, square, sh, shsq, lfn with {
	lfo1 = os.osc(f/2);
  square = os.lf_squarewave(2*f);
	sh = no.noise : ba.sAndH(os.lf_imptrain(f/4));
  shsq = square, sh : si.interpolate(mix);
	lfn = no.lfnoise(f);
};

synth.code:
import("stdfaust.lib");

gui(x) = hgroup("Forced Van der Pol", x);
uiSource(x) = gui(hgroup("[1]Forcing Source", x));

uiDensity = uiSource(vslider("[0]Density [scale:log]", 0.7, 0.1, 16000, 0.01));
uiFreq = uiSource(vslider("[1]Freq [unit:Hz] [scale:log]", 55, 0.1, 16000, 0.01)) : qu.quantize(11, qu.mixo) : si.smoo;
uiQ = uiSource(vslider("[2]Q", 14, 1.1, 20, 0.01)) : ^(4) : si.smoo;
uiStrike(x) = uiSource(hgroup("[4]Strike Model", x));
uiSPos = 0.2; // uiStrike(vslider("[1]Position", 0.2, 0, 1, 0.01)) : si.smoo;
uiSSharp = 0.2; // uiStrike(vslider("[2]Sharpness", 0.2, 0, 1, 0.01)) : si.smoo;
uiSGain = 0.2; // uiStrike(vslider("[3]Gain", 0.2, 0, 1, 0.01)) : si.smoo;

uiVanDerPol(x) = gui(hgroup("[2]VdP Oscillator", x));
uiVdPInputs(x) = uiVanDerPol(hgroup("[1]Inputs", x));
uiForce = uiVdPInputs(vslider("[0]Force", 0.5, 0.001, 1, 0.01)) : ^(2) : *(100) : si.smoo;
uiEps = uiVdPInputs(vslider("[1]Feedback", 0.1, 0, 1, 0.00001)) : ^(2) : *(50) : si.smoo;
uiDt = uiVanDerPol(vslider("[2]dt", 1, 0.1, 20, 0.001)) : /(50) : si.smoo;
uiLim = uiVanDerPol(vslider("[3]Limit",  0.1, 0.0001, 1, .000001)) : ^(2) : *(100) : si.smoo;
uiHP = uiVanDerPol(vslider("[4]DC Block [scale:log]", 10, 0.5, 220, 0.001)) : si.smoo;
uiDrive = uiVanDerPol(vslider("[6]Drive", 3, 0.001, 11, 0.01)) : si.smoo;
uiBalance = uiVanDerPol(vslider("[7]L/R Balance", 0.42, 0, 1, 0.01)) : si.smoo;

// Generator
osc = no.sparse_noise(uiDensity) : pm.strike(uiSPos, uiSSharp, uiSGain) : fil(uiFreq, uiQ) with {
    fil(freq, q) = fi.nlf2(freq, 1 - 1/q) : _,!;
};

// Forced Van der Pol oscillator
fvdp(o) = o : (loop ~ (_,_))  : par(i, 2, out) with {
    // signal conditioning block
    // (saturation with variable limit) -> (dc blocker with variable frequency)
    sblock(x) = x : /(uiLim) : ma.tanh : *(uiLim) : fi.highpass(1, uiHP);

    loop(x, y, osc) = sblock(x1), sblock(y1) with {
        x1 = x + uiDt * y;
        y1 = y + uiDt * (uiEps * y * (1 - x^2) - x + uiForce * osc);
    };

    out = /(uiLim) : *(uiDrive) : ma.tanh;
};

omix(x, y) = l, r with {
    l = x, y : si.interpolate(uiBalance);
    r = x, y : si.interpolate(1-uiBalance);
};

process = osc : fvdp : omix;
