synth.code:
import("stdfaust.lib");

ui(x) = tgroup("Euclid Van der Pol", x);
uiseq(x) = ui(hgroup("[1]Sequencer", x));

N = uiseq(vslider("[0]N", 15, 2, 21, 1));
CLK = uiseq(vslider("[0]Clock [scale:log] [unit: bpm]", 200, 10, 900, 0.1)) : /(60) : si.smoo;
K1 = uiseq(vslider("[1]K1", 2, 2, 21, 1)) : min(N);
K2 = uiseq(vslider("[4]K2", 7, 2, 21, 1)) : min(N);
K3 = uiseq(vslider("[7]K3", 9, 2, 21, 1)) : min(N);

clock = os.lf_imptrain(CLK);

/* 
ideal syntax below (maybe write a pre-processor)

counter(imp) = trig with {
    i = (i' + imp) % n;
    e = ba.if(imp, descent, e');
    descent = (k * i') % n >= (k * i) % n;
    trig = imp * e;
}
*/

euclid(n, k, clock) = clock : counter : _ with {
    counter(imp) = (_counter ~ (_,_))(imp) : !,!,_ with {
        _counter(_i, _e, imp) = i, e, trig with {
            i = (_i + imp) % n;
            e = ba.if(imp, descent, _e);
            descent = (k * _i) % n >= (k * i) % n;
            trig = imp * e;
        };
    };
};

uio1(x) = ui(hgroup("[2]Osc 1", x));
uio2(x) = ui(hgroup("[3]Osc 2", x));
uio3(x) = ui(hgroup("[4]Osc 3", x));

quant = qu.quantize(55, qu.mixo);

// Forced Van der Pol oscillator
fvdp(trig) = trig : resonator <: dry, wet : interprod with {
  dry = _;
  wet = _ : (loop ~ (_,_)) : par(i, 2, out) : spread;  

  // driving source
  resonator(trig) = trig : pm.strike(0.2, 0.2, 0.5) : fi.nlf2(uiFreq, 1 - 1/uiQ) : _,!;

  // signal conditioning block
  // (saturation with variable limit) -> (dc blocker with variable frequency)
  sblock(x) = x : /(uiLim) : ma.tanh : *(uiLim) : fi.highpass(1, uiHP);

  loop(x, y, osc) = sblock(x1), sblock(y1) with {
    x1 = x + uiDt * y;
    y1 = y + uiDt * (uiEps * y * (1 - x^2) - x + uiForce * osc);
  };

  out = /(uiLim) : *(uiDrive) : ma.tanh;

  spread(x, y) = x, y : si.interpolate(uiSpread);

  // 3-way mixer, interpolates between 3 signals based on a [0..1] value
  mix3(v, x, y, z) = gX * x + gY * y + gZ * z with {
      gX = ba.listInterp((1, 0, 0), v * 2);
      gY = ba.listInterp((0, 1, 0), v * 2);
      gZ = ba.listInterp((0, 0, 1), v * 2);
  };

  // interpolate between two signals through their product
  interprod(a, b) = mix3(uiDryWet, a, a*b, b);

  uiFreq = vslider("[1]Freq [unit:Hz] [scale:log]", 55, 0.1, 16000, 0.01) : quant : si.smoo;
  uiQ = vslider("[2]Q", 14, 1.1, 20, 0.01) : ^(4) : si.smoo;

  uiForce = vslider("[3]Force", 0.5, 0.001, 1, 0.01) : ^(2) : *(100) : si.smoo;
  uiEps = vslider("[4]Feedback", 0.1, 0, 1, 0.00001) : ^(2) : *(50) : si.smoo;
  uiDt = vslider("[5]dt", 1, 0.1, 20, 0.001) : /(50) : si.smoo;
  uiLim = vslider("[6]Limit",  0.1, 0.0001, 1, .000001) : ^(2) : *(100) : si.smoo;
  uiHP = vslider("[7]DC Block [scale:log]", 10, 0.5, 220, 0.001) : si.smoo;
  uiDrive = vslider("[8]Drive", 0.3, 0, 1, 0.001) : ^(4) : *(11) : si.smoo;

  uiSpread = vslider("[90]Spread", 0.42, 0, 1, 0.01) : si.smoo;
  uiDryWet = vslider("[91]DryWet", 0.21, 0, 1, 0.01) : si.smoo;
};

eseq(clock) = clock <: euclid(N, K1), euclid(N, K2), euclid(N, K3);

process = clock : eseq : uio1(fvdp), uio2(fvdp), uio3(fvdp) :> /(3) <: ui(dm.zita_light) : _,_;
